import React from 'react'
import { useNavigate } from 'react-router-dom'
import { useState, useEffect } from 'react'
import '../css/NavBar.css'

const NavBar = () => {
    const navigate = useNavigate()
    const [prevScrollPos, setPrevScrollPos] = useState(0);
    const [visible, setVisible] = useState(true);

    useEffect(() => {
        const handleScroll = () => {
            const currentScrollPos = window.pageYOffset;
            setVisible(prevScrollPos > currentScrollPos || currentScrollPos < 10);
            setPrevScrollPos(currentScrollPos);
        };

        window.addEventListener('scroll', handleScroll);

        return () => window.removeEventListener('scroll', handleScroll);
    }, [prevScrollPos, visible]);

    const navbarStyle = {
        transition: 'top 0.6s',
        top: visible ? '0' : '-100px',
    };


    return (
        <div>
            <nav className="navbar NavBarCSS navbar-expand-xl p-4 fixed-top" style={{navbarStyle}}>
                <div className="container-fluid gap-1">
                    <a className="navbar-brand display-5" href="#">Fabriko</a>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNavAltMarkup"
                        aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation" >
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarNavAltMarkup" >
                        <ul className="navbar-nav me-auto gap-3">
                            <li className="nav-item">
                                <a className="nav-link active" aria-current="page" href="#">Home</a>
                            </li>
                            <li className="nav-item dropdown">
                                <a className="nav-link dropdown-toggle" href="#" id="navbarDropdownMenuLink" role="button"
                                    data-bs-toggle="dropdown" aria-expanded="false">
                                    Categories
                                </a>
                                <ul className="dropdown-menu" aria-labelledby="navbarDropdownMenuLink">
                                    <a className="dropdown-item" href="#">Category 1</a>
                                    <a className="dropdown-item" href="#">Category 2</a>
                                    <a className="dropdown-item" href="#">Category 3</a>
                                </ul>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="#">Contact us</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="#">Shopping Cart</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="#">Log out</a>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav>
        </div>
    )
}

export default NavBar
