import React from 'react'
import FormProduct from './FormProduct'
import { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import { useParams } from 'react-router-dom'


const EditProduct = (props) => {
    const { product, setProduct, setProductId } = props;
    const [productInfo, setProductInfo] = useState({
        productId: "",
        productName: "",
        productPrice: "",
        productDescription: "",
        productCategory: "",
        productImage1: null,
        productImage2: null,
        productImage3: null,
        productImage4: null,
        productImage5: null
    })
    const [errors, setErrors] = useState({})
    const navigate = useNavigate()
    const { productId } = useParams()

    const changeHandler = (e) => {
        if (e.target.type === "file") {
            setProductInfo({
                ...productInfo,
                [e.target.name]: e.target.files[0]
            })
        } else {
            setProductInfo({
                ...productInfo,
                productId: productId,
                [e.target.name]: e.target.value
            })
        }
    }

    useEffect(() => {
        fetch("http://localhost:8080/product/" + productId)
            .then(res => res.json())
            .then(res => {
                setProduct(res)
            })
            .catch(err => console.log(err))
    }, [])


    const editProduct = (e) => {
        const formData = new FormData()
        formData.append("productName", productInfo.productName)
        formData.append("productPrice", productInfo.productPrice)
        formData.append("productDescription", productInfo.productDescription)
        formData.append("productCategory", productInfo.productCategory)
        formData.append("productImage1", productInfo.productImage1)
        formData.append("productImage2", productInfo.productImage2)
        formData.append("productImage3", productInfo.productImage3)
        formData.append("productImage4", productInfo.productImage4)
        formData.append("productImage5", productInfo.productImage5)
        e.preventDefault()
        fetch(`http://localhost:8080/product/edit/` + productId, {
            method: "POST",
            body: formData
        })
            .then(res => {
                console.log(res)
                if (res.data && res.data.errors) {
                    setErrors(res.data.errors)
                } else {
                    setProductInfo({
                        productName: "",
                        productPrice: "",
                        productDescription: "",
                        productCategory: "",
                        productImage1: "",
                        productImage2: "",
                        productImage3: "",
                        productImage4: "",
                        productImage5: ""
                    })
                    setErrors({})
                }
            })
            .catch(err => console.log(err))
    }

    return (
        <div>
            <FormProduct
                message={"Edit Product"}
                productInfo={productInfo}
                setProductInfo={setProductInfo}
                changeHandler={changeHandler}
                submitHandler={editProduct}
                errors={errors}
                submitValue={"Edit Product"}
            />
        </div>
    )
}

export default EditProduct
