import React from 'react'
import { useState, useEffect } from 'react'
import SuitsPic from '../images/suitsPic.jpeg'
import windowOld from '../images/windowOld.jpeg'
import AllCategories from './AllCategories'
import pictureSwitch1 from '../images/pictureSwitch1.png'
import pictureSwitch2 from '../images/pictureSwitch2.png'
import pictureSwitch3 from '../images/pictureSwitch3.png'
import pictureSwitch4 from '../images/pictureSwitch4.png'
import pictureSwitch5 from '../images/pictureSwitch5.png'
import pictureSwitch6 from '../images/pictureSwitch6.png'
import pictureSwitch7 from '../images/pictureSwitch7.webp'
import pictureSwitch8 from '../images/pictureSwitch8.png'

const Home = () => {
    const [currentPicture, setCurrentPicture] = useState(0);
    const pictures = [
        pictureSwitch1,
        pictureSwitch2,
        pictureSwitch3,
        pictureSwitch4,
        pictureSwitch5,
        pictureSwitch6,
        pictureSwitch7,
        pictureSwitch8,
    ];

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentPicture((currentPicture) => (currentPicture + 1) % pictures.length);
        }, 3000);
        return () => clearInterval(interval);
    }, [pictures.length]);

    useEffect(() => {
        const img = document.getElementById('pictureSwitch');
        if (img) {
            img.style.opacity = 0;
            setTimeout(() => {
                img.style.opacity = 1;
                img.style.transition = 'opacity 1s ease-in-out';
            }, 100);
        }
    }, [currentPicture]);
    return (
        <div>
            <div style={{
                backgroundImage: `url(${SuitsPic})`,
                backgroundSize: 'cover',
                height: '100vh',
                width: '100vw',
                position: 'relative'
            }}>
                <h1 style={{
                    position: 'absolute',
                    bottom: '20%',
                    left: '5%',
                    color: 'black',
                    // fontFamily: 'Roboto',
                    fontWeight: 100
                }}>Unlock Your Style</h1>
                <h4 style={{
                    position: 'absolute',
                    bottom: '15%',
                    left: '5%',
                    color: 'black',
                    // fontFamily: 'Roboto',
                    fontWeight: 100
                }}>Fabriko - Where Elegance Reigns.</h4>
            </div>
            <div className='container-fluid' style={{ backgroundColor: "#F5F4EF" }}>
                <div className='row d-flex justify-content-around p-5'>
                    <div className='col-4 p-3'>
                        <img src={windowOld} alt='windowOld' style={{ width: '70%' }} />
                    </div>
                    <div className='col-4 mt-5'>
                        <h1 style={{ fontWeight: 300, textDecoration: 'underline', color: "#212727" }}>About Us</h1>
                        <p className='mt-4' style={{ fontWeight: 300, color: "#212727" }}>Since 1937, Fabriko has epitomized elegance in men's fashion. Our timeless suits, crafted with meticulous attention to detail, have adorned influential leaders and stylish icons. With a blend of tradition and innovation, Fabriko redefines the essence of sophistication. Step into our world and experience sartorial excellence.</p>
                    </div>
                </div>
            </div>
            <AllCategories />
            <div className="container-fluid" style={{ backgroundColor: "#F5F4EF", height: "10%" }}>
                <div className="d-flex justify-content-center align-items-center">
                    <div className="col-4 offset-3 p-5 text-center">
                        <h2 style={{ fontWeight: 300, color: "#212727" }}>Fit to Perfection Guaranteed</h2>
                        <p className="mt-4" style={{ fontWeight: 300, color: "#212727" }}>From fabrics and buttons to pocket styles and lining colors, personalize your handcrafted look. Take control and feel confident with our Perfect Fit Guarantee.</p>
                    </div>
                    <div className="row d-flex justify-content-around p-5">
                        <div className="col-8 p-3">
                            <img src={pictures[currentPicture]} alt={`pictureSwitch${currentPicture}`} style={{ width: '100%', opacity: 1 }} />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Home
