import React from 'react'
import { useState, useEffect } from 'react'
import { useNavigate, Link } from "react-router-dom";

const Log = (props) => {
    const { sessionId, setSessionId } = props
    const [user, setUser] = useState({
        email: "",
        password: ""
    })
    const [errors, setErrors] = useState({})
    const navigate = useNavigate()

    const changeHandler = (e) => {
        setUser({
            ...user,
            [e.target.name]: e.target.value
        })
    }

    const submitLog = (e) => {
        e.preventDefault()
        fetch("http://localhost:8080/api/login", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(user)
        })
            .then(res => {
                console.log(res)
                if (res.data && res.data.errors) {
                    setErrors(res.data.errors)
                } else {
                    setUser({
                        email: "",
                        password: ""
                    })
                    setErrors({})
                    setSessionId(res.data.sessionId)
                    navigate("/dashboard")
                }
            })
            .catch(err => console.log(err))
    }




    return (
        <div className='container d-flex justify-content-center' style={{ padding: '10%' }}>
            <div className='col-md-6'>
                <h1 className='text-center mb-4' style={{ fontWeight: 300 }}>Login</h1>
                <form onSubmit={submitLog}>
                    <div className='form-floating mb-3'>
                        <input type='email' className='form-control' id='email' placeholder='Email' name='email' value={userInfo.email} onChange={changeHandler} />
                        <label htmlFor='email'>Email</label>
                    </div>
                    <div className='form-floating mb-3'>
                        <input type='password' className='form-control' id='password' placeholder='Password' name='password' value={userInfo.password} onChange={changeHandler} />
                        <label htmlFor='password'>Password</label>
                    </div>
                    <button type='submit' className='btn btn-outline-dark'>Login</button>
                    <div className='row mt-3'>
                        <div className='col text-center'>
                            <Link className='text-dark' to='/register'>Don't have an account? Register here.</Link>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default Log
