                <p>Product Price</p>
            </div>
            <div className="col-sm-4">
                <Link to="/product/6">
                    <img src={UpperWear6} alt="UpperWear6" width="300" height="300" />
                </Link>
                <p>Product Name</p>
                <p>Product Price</p>
            </div>
        </div>
        <div className="row">
            <div className="col-sm-4">
                <Link to="/product/7">
                    <img src={UpperWear7} alt="UpperWear7" width="300" height="300" />
                </Link>
                <p>Product Name</p>
                <p>Product Price</p>
            </div>
        </div>
    </div>
  )
}

export default UpperCategory
