import React from 'react'
import { useState } from 'react'

const ContactUs = () => {
    const [contactInfo, setContactInfo] = useState({
        name: '',
        email: '',
        phoneNumber: '',
        message: ''
    })
    const [errors, setErrors] = useState({})

    const onChange = (e) => {
        setContactInfo({
            ...contactInfo,
            [e.target.name]: e.target.value
        })
    }

    const onSubmit = (e) => {
        e.preventDefault()
        console.log(contactInfo)
        fetch('http://localhost:8080/api/contact', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(contactInfo)
        })
            .then(res => {
                console.log(res)
                if (res.data && res.data.errors) {
                    setErrors(res.data.errors)
                } else {
                    setContactInfo({
                        name: '',
                        email: '',
                        phoneNumber: '',
                        message: ''
                    })
                    setErrors({})
                }
            })
            .catch(err => console.log(err))
    }



    return (
        <div className='container d-flex justify-content-center' style={{ padding: '14%' }}>
            <div className='col-md-6'>
                <div className='card'>
                    <div className='card-body'>
                        <h5 className='card-title mb-3' style={{ fontWeight: 300 }}>Contact Us</h5>
                        <div className='d-flex flex-column'>
                            <div className='d-flex flex-row align-items-center mb-2'>
                                <h6 className='mr-2 font-weight-bold'>Address:</h6>
                                <p className='mb-0'>20892 Abner Trace, Gloverstad, TN 33484</p>
                            </div>
                            <div className='d-flex flex-row align-items-center mb-2'>
                                <h6 className='mr-2 font-weight-bold'>Office:</h6>
                                <p className='mb-0'>1 (818) 970 - 3118</p>
                            </div>
                            <div className='d-flex flex-row align-items-center'>
                                <h6 className='mr-2 font-weight-bold'>Email:</h6>
                                <p className='mb-0'>customersupport@fabriko.com</p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ContactUs
